package trico.eve.api.error;import static org.hamcrest.CoreMatchers.is;import static org.junit.Assert.assertThat;import org.junit.Before;import org.junit.Test;import trico.eve.api.FakeResponseFile;/** * @author nico.mainka */public class ErrorTest {	private Error error;	private FakeResponseFile testResponseFile;	@Before	public void setUp() throws Exception {		testResponseFile = new FakeResponseFile("error.xml");		error = new Error(testResponseFile.getContent());	}	@Test	public void test() {		System.out.println(error);	}	@Test	public void isError() throws Exception {		assertThat(Error.isError(testResponseFile.getContent()), is(true));	}	@Test	public void isNotError() throws Exception {		assertThat(Error.isError(new FakeResponseFile("orders.xml").getContent()), is(false));	}}